import { FaEye, FaEyeSlash } from "react-icons/fa";
import { useState } from "react";
 // import React, { useState } from "react";
function Form() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch("http://localhost:5000/submit", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email }),
    })
      .then((res) => res.text())
      .then((data) => {
        alert(data); // shows success message
        setName("");
        setEmail("");
      })
      .catch((err) => console.error("Error:", err));
  };
    const [password, setPassword] = useState(false);
    const setNameHandler    = () => {
      setPassword(prev=> !prev);
    }
    return(
        <>
        <form id='form' onSubmit={handleSubmit}>
            <div id='heading'><h1>Registration Form</h1></div>

            <label htmlFor="name">Name:</label>
            <input type="text" placeholder="Enter your name"/>
            <br/>
            <label htmlFor="email">Email:</label>
            <input type="email" placeholder="Enter your email"/>
            <br/>
            <div className="password-container">

            <label htmlFor="password">Password:</label>
            <input type={password ? "text" : "password"}
             placeholder="Enter your password"/>
             <span className="password-toggle-icon" onClick={setNameHandler }>
          {password ? <FaEye /> : <FaEyeSlash />}
         </span> 
            <br/>
            <label htmlFor="confirm-password">Confirm Password:</label>
            <input type="password" placeholder="Confirm your password"/>
            <span className="password-toggle-icon" onClick={setNameHandler }>
          {password ? <FaEye /> : <FaEyeSlash />}
        </span>
        </div>
            <br/>
            <label htmlFor="roll-number">Roll-number:</label>
            <input type="text" placeholder="Enter your roll number"/>
            <br/>
            <label htmlFor="department">Department:</label>
            <input type="text" placeholder="Enter your department"/>
            <br/>
            <label htmlFor="session">Session:</label>
            <input type="number" placeholder="Enter your session"/>
            <br/>
            <button type="submit" >Submit</button>
        </form>
        </>
    );
}
export default Form;
